import { Component, Input, OnInit } from '@angular/core';

@Component({
  selector: 'app-clock',
  templateUrl: './clock.component.html',
  styleUrls: ['./clock.component.scss']
})
export class ClockComponent implements OnInit{
  @Input() city: string = 'Jerusalem';
  @Input() time: Date = new Date();
  @Input() difference: number = 0;

  second = 0;
  hour = 0;
  minute = 0;
  d = new Date();
  hourHand = new Document();

  ngOnInit(): void {
    // setInterval(() => {

    //   this.d = new Date();
    //   this.second = this.d.getSeconds() * 6;
    //   this.minute = this.d.getMinutes() * 6;
    //   this.hour = this.d.getHours() * 30 + Math.round(this.minute / 12);
    // }, 1000)

    this.setClock();

    this.hourHand = document.querySelector('[data-hour-hand]')
    minuteHand = document.querySelector('[data-minute-hand]')
    secondHand = document.querySelector('[data-second-hand]')
  }

  setClock() {
    const currentDate = new Date();
    const secondsRatio = currentDate.getSeconds() / 60;
    const minutesRatio = (secondsRatio + currentDate.getMinutes()) / 60;
    const hoursRatio = (minutesRatio + currentDate.getHours()) / 12;
    this.setRotation(this.secondHand, secondsRatio);
    this.setRotation(this.minuteHand, minutesRatio);
    this.setRotation(this.hourHand, hoursRatio);
  }

  setRotation(element: Element | null, rotationRatio: number) {
    let rotationToSet = rotationRatio * 360;
    let styleAttributeToSet = '--rotation: ' + rotationToSet.toString();
    element?.setAttribute("style",styleAttributeToSet);
    // element.style.setProperty('--rotation', rotationRatio * 360)
  }

  // this.setClock();
////////////////////////////////////////////
  get hourTransform() {
    // const hours = this.time.getHours() + this.difference;
    const hours = this.hour + this.difference;
    const degrees = (hours / 12) * 360 + 90; // add 90 degrees to start at 12 o'clock
    return `rotate(${degrees}deg)`;
  }

  get minuteTransform() {
    const minutes = this.minute;
    const degrees = (minutes / 60) * 360 + 90;
    return `rotate(${degrees}deg)`;
  }

  get secondTransform() {
    const seconds = this.second;
    const degrees = (seconds / 60) * 360 + 90;
    return `rotate(${degrees}deg)`;
  }
}
