import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.scss']
})
export class AppComponent implements OnInit{
  title = 'four-cities-clock';
  jerusalemTime = new Date();
  ngOnInit(): void {
    const now = new Date(); // current date and time
    const jerusalemTime = new Date(now.getTime() + (2 * 60 * 60 * 1000)); // add 2 hours in milliseconds

  }

}
<app-clock [city]="'Jerusalem'" [time]="jerusalemTime" [difference]="0"></app-clock>
<app-clock [city]="'Jerusalem'" [time]="jerusalemTime" [difference]="6"></app-clock>
