import { Component, Input, OnInit } from '@angular/core';

@Component({
  selector: 'app-clock',
  templateUrl: './clock.component.html',
  styleUrls: ['./clock.component.scss']
})
export class ClockComponent implements OnInit{
  @Input() city: string = 'Jerusalem';
  @Input() time: Date = new Date();
  @Input() difference: number = 0;

  second = 0;
  hour = 0;
  minute = 0;
  d = new Date();

  ngOnInit(): void {
    setInterval(() => {

      this.d = new Date();
      this.second = this.d.getSeconds() * 6;
      this.minute = this.d.getMinutes() * 6;
      this.hour = this.d.getHours() * 30 + Math.round(this.minute / 12);
    }, 1000)
  }
  get hourTransform() {
    const hours = this.time.getHours() + this.difference;
    const degrees = (hours / 12) * 360 + 90; // add 90 degrees to start at 12 o'clock
    return `rotate(${degrees}deg)`;
  }

  get minuteTransform() {
    const minutes = this.time.getMinutes();
    const degrees = (minutes / 60) * 360 + 90;
    return `rotate(${degrees}deg)`;
  }

  get secondTransform() {
    const seconds = this.time.getSeconds();
    const degrees = (seconds / 60) * 360 + 90;
    return `rotate(${degrees}deg)`;
  }
}
<div class="clock">
  <h2>{{ city }}</h2>
  <div class="analog-clock">
    <div class="hour-hand" [style.transform]="hourTransform"></div>
    <div class="minute-hand" [style.transform]="minuteTransform"></div>
    <div class="second-hand" [style.transform]="secondTransform"></div>
    <div class="clock-face"></div>
  </div>
  <div class="digital-clock">{{ time | date: 'h:mm:ss a' }}</div>
</div>
